import React, { Component } from "react";
import axios from "axios";

export default class FormFilm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      moviename: "",
      postername: "",
      comment: "",
    };
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  submitForm(e) {
    e.preventDefault();
    const url = "https://post-a-form.herokuapp.com/api/movies/"
    axios.post(url, this.state)
    .then(res => res.data)
    .then(res => {
        alert(`Film ajouté avec l'ID ${res.id} !`);

    })
  }
  render() {
    return (
      <div className="FormFilm">
        <h1>Saisie d'un film</h1>

        <form onSubmit={this.submitForm}>
          <fieldset>
            <legend>Informations</legend>
            <div className="form-data">
              <label htmlFor="moviename">Nom du film</label>
              <input
                type="text"
                id="moviename"
                name="moviename"
                onChange={this.onChange}
                value={this.state.moviename}
              />
            </div>

            <div className="form-data">
              <label htmlFor="postername">Affiche du film</label>
              <input
                type="url"
                id="postername"
                name="postername"
                onChange={this.onChange}
                value={this.state.postername}
              />
            </div>

            <div>
              <legend>Your comments : </legend>
              <textarea
                name="comment"
                placeholder="why do you like this movie ?"
              ></textarea>
            </div>

            <hr />
            <div className="form-data">
              <input type="submit" value="Envoyer" />
            </div>
          </fieldset>
        </form>
      </div>
    );
  }
}
